import { createSlice } from '@reduxjs/toolkit';
import { STATES } from 'mongoose';


const initialState = {
    currentUser: null,
    error: null,
    loading: false,
};

const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {
        signInStatr: (start) => {
            state.loading = true;
        },
        signInSuccess: (state, action) => {
            state.currentUser = action.payload;
            state.loading = false;
            state.error = null;
        },
        signInFailure: (state, action) => {
            state.error = action.payload;
            state.loading = false;
        }

    }
});

export const {signInStatr,signInSuccess,signInFailure} = userSlice.actions;

export default userSlice.reducer;
